<script>
export default {
  data() {
    let location = encodeURI(window.location.href);
    let text = encodeURI('Acesse nossa calculadora:');

    return {
      staticHearderHidden: false,
      facebook: `https://www.facebook.com/sharer.php?u=${location}`,
      linkedin: `https://www.linkedin.com/shareArticle?url=${location}&title=${text}`,
      twitter: `https://twitter.com/share?url=${location}&text=${text}`,
      whatsapp: `https://api.whatsapp.com/send?text=${location}`,
    };
  },

  methods: {
    gotoSamydanaCalc() {
      let y = this.$refs.calculadora.getBoundingClientRect().top + window.pageYOffset - 4;
      window.scrollTo({top: y, behavior: 'smooth'});
      // document.querySelector('#samydana-calc').scrollIntoView({ behavior: 'smooth', block: 'start'});
    },

    isInViewport(element) {
      const viewporBottom = Math.max(document.documentElement.clientHeight || 0, window.innerHeight || 0);
      const rect = element.getBoundingClientRect();
      return (rect.bottom>=0 && rect.bottom<=viewporBottom) || (rect.top>=0 && rect.top<=viewporBottom);
    },

    windowScrollHandle(ev) {
      if (this.windowScrollHandleTimeout) clearTimeout(this.windowScrollHandleTimeout);
      this.windowScrollHandleTimeout = setTimeout(() => {
        this.staticHearderHidden = this.isInViewport(this.$refs.static);
      }, 200);
    }
  },

  mounted() {
    this.staticHearderHidden = this.isInViewport(this.$refs.static);
    window.addEventListener('scroll', this.windowScrollHandle);
  },

  beforeDestroy() {
    window.removeEventListener('scroll', this.windowScrollHandle);
  },
}
</script>